mapper.select:
SELECT SUM(quantity)
FROM order_detail
WHERE dish_id = #{param};


SELECT o.user_id
FROM `order` AS o 
JOIN order_detail AS od
ON o.order_id = od.order_id
WHERE od.dish_id = #{param}
GROUP BY o.user_id
HAVING SUM(quantity) = (
    SELECT MAX(customer_total_quantity)
    FROM(
        SELECT SUM(sub.quantity) AS customer_total_quantity
        FROM  order_detail AS sub
        WHERE dish_id = #{param}
        GROUP BY user_id                    
    ) AS max_query
);

SELECT SUM(od.quantity) 
FROM order_detail AS od 
JOIN `order` AS o 
ON o.order_id = od.order_id 
WHERE od.dish_id = #{DishId} 
AND o.create_at >= #{Timestamp} 
AND o.order_method = #{OrderMethod};

SELECT od.dish_id,SUM(od.quantity) AS total_purchase
FROM order_detail AS od 
JOIN `order` AS o 
ON o.order_id = od.order_id 
WHERE o.restaurant_id = #{restaurantId} 
AND o.create_at >= #{startTime} 
AND o.user_id = #{userId}
GROUP BY od.dish_id;
