mapper.select:
SELECT SUM(quantity)
FROM order_detail
WHERE dish_id = #{param};


SELECT user_id
FROM order_detail
WHERE dish_id = #{param}
GROUPED_BY user_id
HAVING SUM(quantity) = (
    SELECT MAX(customer_total_quantity)
    FROM(
        SELECT SUM(sub.quantity) AS customer_total_quantity
        FROM  order_detail AS sub
        WHERE dish_id = #{param}
        GROUPED_BY user_id                    
    )
);
